import axios from "axios";
import {useEffect, useState} from "react";
import "../AllTablesComponent/AllTablesComponent.css"
import "./Exercitiu3a.css"

class Bilete {
    constructor(clasa, sursa, destinatia) {
        this.clasa = clasa;
        this.sursa = sursa;
        this.destinatia = destinatia;
    }
}

function createBiletRow(bileteData) {
    return (
        <tr key={bileteData.clasa}>
            <td>{bileteData.clasa}</td>
            <td>{bileteData.sursa}</td>
            <td>{bileteData.destinatia}</td>
        </tr>
    )
}

function Exercitiu3aTable() {
    async function getBilete() {
        try {
            const response = await axios.get('http://localhost:3001/bilete/3a');
            setBileteData(response.data.map(b => new Bilete(b.clasa, b.sursa, b.destinatia)));
            console.log(Bilete);
        } catch (error) {
            console.log(error);
        }
    }

    const [bileteData, setBileteData] = useState([]);
    useEffect(() => {getBilete()}, [])

    return (
        <>
            <div className="center">
                <table className="tabelForm">
                    <thead>
                    <tr>
                        <th colSpan={3} id="numetabel">Tabel Exercitiu 3a</th>
                    </tr>
                    <tr className="tableFormColor">
                        <td>Clasa</td>
                        <td>Sursa</td>
                        <td>Destinatie</td>
                    </tr>
                    </thead>
                    <tbody>
                    {bileteData.map(bileteData => createBiletRow(bileteData))}
                    </tbody>
                </table>
            </div>
        </>
    );
}
export default Exercitiu3aTable;
